import "./App.css";
import DiaryEditor from "./component/DiaryEditor/DiaryEditor";
import DiaryList from "./component/DiaryList/DiaryList";
import { useRef, useState } from "react";

function App() {
  const [data, setData] = useState([]);
  const dataId = useRef(0);
  const onCreate = (author, contents, emotion) => {
    const created_date = new Date().getTime();

    const newItem = {
      author,
      contents,
      emotion,
      created_date,
      id: dataId.current,
    };
    dataId.current += 1;
    setData([newItem, ...data]);
  };
  const onEdit = (targetId, newContents) => {
    setData(
      data.map((d) => (d.id === targetId ? { ...d, contents: newContents } : d))
    );
  };

  const onDelete = (targetId) => {
    const withoutDeletedItem = data.filter((it) => it.id !== targetId);
    setData(withoutDeletedItem);
  };

  return (
    <>
      <DiaryEditor onCreate={onCreate} />
      <DiaryList onDelete={onDelete} onEdit={onEdit} diaryList={data} />
    </>
  );
}

export default App;
